  /**
   * The name of the sink.
   */
  name: Generated<string>;
  /**
   * A [PostgreSQL-compatible OID][`oid`] for the sink.
   */
  oid: Generated<number>;
  /**
   * The role ID of the owner of the sink. Corresponds to `mz_roles.id`.
   */
