  /**
   * The size of the sink.
   */
  size: Generated<string | null>;
  /**
   * The type of the sink: `kafka`.
   */
  type: Generated<string>;
  /**
   * The format of the Kafka message value for messages produced by the sink: `avro`, `json`, `text`, or `bytes`.
   */
